TIMER_PERIOD=1/64
HERO={}
function InitHeroTable(hero)
    perebor=CreateGroup()
    HERO[0]={
        UnitHero=hero,
        ReleaseW=false,
        ReleaseS=false,
        ReleaseD=false,
        ReleaseA=false,
        ReleaseLMB=false,
        ReleaseRMB=false,
        isAttacking=false,
        isCharging=false,
        isMoving=false,
        DropInventory=true,
        isShield=false,
        ShieldEff=nil,
    }
end


function InitWASD(hero)
    --print("initwasdSTART")
    InitHeroTable(hero)
    CreateWASDActions()
    BlockMouse()
    EnableDragSelect(false, false)
    BlzEnableSelections(false, false)
    local data=HERO[0]
    local angle=0

    local speed=5
    local animWalk=0
