                        if  GetUnitTypeId(hero)~=FourCC("Edmm") then
                            ResetUnitAnimation(hero)
                            --print("дефолтный сборс")
                        else
                            -- print("сборс анимации мышей")
                        end
                        animStand=0
                    end
                    --end
                    --print("r")--..GetUnitName(mainHero)
                end
            else
